def generate_and_count_rows(n):
    l1 = [2**n]
    stab = (max(l1) < 2)
    layer_count = 1
    while not stab:
        l2 = [l1[0]//2]
        for i in range(1, len(l1)):
            l2.append(l1[i]//2 + l1[i-1]//2)
        l2.append(l1[len(l1)-1]//2)
        l1 = l2
        if (max(l1) < 2):
            stab = True
        if l1[0] == 0:
            l1 = l1[1:]
        if l1[-1] == 0:
            l1 = l1[:len(l1)-1]
        layer_count+= 1
    return layer_count
