from sympy import divisor_sigma
def proper_div_sum(n): return divisor_sigma(n) - n
def isok(n):
    sn = proper_div_sum(n)
    ssn = proper_div_sum(sn)
    return abs(sn - n) < abs(ssn - sn)
print([n for n in range(2, 300) if isok(n)])

