def digit_sum(n, b):
    s = 0
    while n > 0:
        s += n % b
        n //= b
    return s
def same_digit_sum_3_5_7(n):
    s3 = digit_sum(n, 3)
    s5 = digit_sum(n, 5)
    if s5 != s3:
        return False
    s7 = digit_sum(n, 7)
    return s7 == s3
print([n for n in range(1, 3000) if same_digit_sum_3_5_7(n)])

