from itertools import combinations
def A390395(n):
    s, t = [], set()
    for b in range(1,n+1):
        for c in range(b+1,n+1):
            a, r = divmod(b*c,b+c)
            if not r:
                s.append({a,b,c})
                t |= {a,b,c}
    l = len(t)
    for i in range(l,-1,-1):
        for d in combinations(t,i):
            if not any(x.issubset(d) for x in s):
                return n-l+i # _Chai Wah Wu_, Nov 19 2025

