# Exponents p with 2^p - 1 prime (A000043), for p <= 127
mersenne_prime_exponents = [2, 3, 5, 7, 13, 17, 19, 31, 61, 89, 107, 127]
def collatz_tripling_steps(n: int) -> int: # A006667(n)
    steps, x = 0, n
    while x != 1:
        if x % 2 == 0:
            x //= 2
        else:
            x = 3*x + 1
            steps += 1
    return steps
