# OEIS sequence: A391107

import math
def count_pairs(n):
    L = 2 * n**2
    count = 0
    for x in range(1, int(math.isqrt(L)) + 1):
        y2 = L - x*x
        y = int(math.isqrt(y2))
        if y*y == y2 and y > x:
            count += 1
    return count
# Generate list of n ≤ MAX with ≥4 pairs
MAX = 1500
vals = [n for n in range(1, MAX+1) if count_pairs(n) >= 4]
print(vals)

