from sympy import prime
def pip(n):
    return prime(prime(n))
def equidistant_pips(max_k=10000):
    terms = []
    indices = []
    q_prev = pip(1)
    q_curr = pip(2)
    for k in range(2, max_k):
        q_next = pip(k + 1)
        if q_curr - q_prev == q_next - q_curr:
            terms.append(q_curr)
            indices.append(k)
        q_prev, q_curr = q_curr, q_next
    return terms, indices

