from itertools import count
from sympy.ntheory import digits, is_palindromic
def fromrevdigits(t, b): return sum(b**i*ti for i, ti in enumerate(t))
def a(n): return next(b for b in count(2) if is_palindromic(n+fromrevdigits(digits(n, b)[1:], b), b))
print([a(n) for n in range(2, 89)]) # _Michael S. Branicky_, Dec 05 2025

