from sympy import isprime
from itertools import count, islice
def ways(n):
    if n%10 not in {1, 2, 3, 5, 7, 9}: return 0
    c, s = int(isprime(n)), str(n)
    for i in range(1, len(s)):
        if s[i]!="0" and isprime(int(s[:i])):
            c += ways(int(s[i:]))
    return c
def agen(): yield from (t for n in count(1) if (t:=ways(n)))
