# OEIS sequence: A391539

def notsirm(mm):
   """
   returns 0 if every integer is a sum of 5**t
    multiplied by coefficients in {-mm,-1,1,mm};  otherwise
    returns the smallest positive integer not fulfilling the above.
   """
   assert isinstance(mm,int)
   mm = abs(mm)
   if mm<2 or mm%5 in (0,1,4): return 2
   digit_dct = {0:0, 1:+1, 4:-1, (+mm)%5:+mm, (-mm)%5:-mm}
   tested_set = {0,1}
   for nn in range(2,mm//4+1):
      if nn in tested_set: continue
      rr = nn
      rr_lst = []
      while rr not in tested_set:
         rr_lst.append(rr)
         rr = abs( (rr-digit_dct[rr%5] )//5 )
         if rr in rr_lst: return nn
      tested_set.update(rr_lst)
   return 0

