from itertools import combinations
from sympy import isprime
def a(n):
    count = 0
    for size in range(n + 1):
        for subset in combinations(range(1, n + 1), size):
            valid = True
            s = list(subset)
            if len(s) >= 2:
                for i in range(len(s)):
                    if not valid:
                        break
                    for j in range(i + 1, len(s)):
                        if isprime(s[i] + s[j]):
                            valid = False
                            break
            if valid:
                count += 1
    return count

