from math import lcm
from sympy import primefactors, prod
def rad(n):
    return 1 if n < 2 else prod(primefactors(n))
def a(n):
    r = rad(n)
    if r == 2:
        return 3
    L = 1
    for p in primefactors(r):
        if p != 2:
            L = lcm(L, p-1)
    return L+1
print([a(n) for n in range(2,78)])

