from itertools import count
from functools import cache
@cache
def b(n, i):
    m = (i*(i+1)//2)**2
    return n <= m and (n==m or b(n+i**3, i-1) or b(abs(n-i**3), i-1))
def a(n): return next(k for k in count(1) if b(n, k))
