from itertools import count
from functools import cache
@cache
def b(nn, n, i):
    m = sum(j**nn for j in range(1, i+1))
    return n <= m and (n==m or b(nn, n+i**nn, i-1) or b(nn, abs(n-i**nn), i-1))
def a(n): return next(k for k in count(1) if b(n, n, k))
