from sympy import isprime
from sympy.ntheory.factor_ import perfect_power
def a(n):
    if n <= 1: return 1
    pp = perfect_power(n)
    p = pp[0] if pp else n
    return p if isprime(p) and (p == 2 or p % 4 == 3) else 1

