import numpy as np
from scipy.spatial import ConvexHull
def T(L, n):
    points = np.argwhere(np.ones((L, L, L)))
    v_idx = []
    for i in range(n):
        if len(points) == 0:
            return 0
        hull = ConvexHull(points)
        v_idx = hull.vertices
        if i < n - 1:
            points = np.delete(points, v_idx, axis=0)
    return len(v_idx)
def a(n):
    return T(2*n + 2, n)//8  # _Chittaranjan Pardeshi_, Jan 03 2026

