from gmpy2 import is_prime, mpz
from itertools import count, product
def a(n):
    s = "".join(str(i) for i in range(n+1))
    cands = set()
    for d in count(1):
        for f in "123456789":
            for w in product("0123456789", repeat=d-1):
                w = "".join(w)
                for i in range(d):
                    v = f + w[:i] + s + w[i:]
                    if is_prime(t:=mpz(v)):
                        cands.add(t)
        if cands:
            return int(min(cands))
