import sympy as sp
def a(n):
    if n <= 1: return 0
    seq = list(range(1, n + 1))
    iters = 0
    while True:
        if all(sp.isprime(seq[i]) != sp.isprime(seq[i+1]) for i in range(len(seq)-1)):
            break
        new_seq = []
        i = 0
        while i < len(seq):
            is_p = sp.isprime(seq[i])
            g_sum = seq[i]
            j = i + 1
            while j < len(seq) and sp.isprime(seq[j]) == is_p:
                g_sum += seq[j]
                j += 1
            new_seq.append(g_sum)
            i = j
        seq = new_seq
        iters += 1
    return iters

